import React from 'react';
import axios from "axios";
import ConvoDetail from './components/ConvoDetail'
import NavBar from './components/navBar'
import './App.css';
import {Container, Row, Column} from 'react-bootstrap'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isCreateConvo: false,
      conversations: [],
      messages: [],
      thoughts: [],
    };
  }

  componentDidMount() {
    axios.get("http://localhost:8000/api/conversations/").then((response) => {
      const conversations = response.data;
      this.setState({ conversations });
    });
  }

  handleFormSubmit = (event) => {
    event.preventDefault();
    const post_content = event.target.elements.post_content.value;
    const boast_or_roast = event.target.elements.boast_or_roast.value;
    axios.post("http://localhost:8000/api/posts/", {
      post_content: post_content,
      boast_or_roast: boast_or_roast,
    });
    axios.get("http://localhost:8000/api/posts/").then((response) => {
      const posts = response.data;
      this.setState({ posts, message: "" });
    });
  };

  onHandleChange = (e) => {
    this.setState({
      message: e.target.value,
    });
  };


  getConvoDetail = (convoId, event) => {
    console.log(convoId)
    // console.log(event)
    // event.preventDefault();
    axios.get(`http://localhost:8000/api/messages/${convoId}`).then((response) => {
      const messages = response.data;
      this.setState({ messages });
    });
  };

  getMessageDetail = (messId, event) => {
    console.log(messId)
    // console.log(event)
    // event.preventDefault();
    axios.get(`http://localhost:8000/api/thoughts/${messId}`).then((response) => {
      const thoughts = response.data;
      this.setState({ thoughts });
    });
  };

  handleDownVote = (postId) => {
    axios.post(`http://localhost:8000/api/posts/${postId}/down_vote/`, {
      pk: postId,
    });
    axios.get("http://localhost:8000/api/posts/").then((response) => {
      const posts = response.data;
      this.setState({ posts });
    });
  };

  handleFeed = (feedName) => {
    axios.get(`http://localhost:8000/api/${feedName}/`).then((response) => {
      const posts = response.data;
      this.setState({ posts });
    });
  };

  toggleNewConvo = () => {
    if(this.state.isCreateConvo){
      this.setState({
        isCreateConvo : false
      })
    }else{
      this.setState({
        isCreateConvo : true
      })
    }

  }
  render() {

    return (
      <Container >
        <NavBar />
      <a href="#" onClick={() => {this.toggleNewConvo()}}><h1>New Conversation</h1></a>
      {this.state.isCreateConvo && <div>ffff</div>}
      {this.state.conversations.map(convo => (
        <div>
        <a href="#" onClick={() => {this.getConvoDetail(convo.id)}}><h1>{convo.title}</h1></a>
        <h3>{convo.start_date}</h3>
        <p>{convo.id}</p>
        </div>
      ))}
      {this.state.messages && <div>
        {this.state.messages.map(mess => (
          <div key={mess.id}>
            <a href="#" onClick={() => {this.getMessageDetail(mess.id)}}>{mess.message}</a>
            <br></br>
            {mess.submission_time}
            <br></br>
            {mess.id}
            </div>

        ))}
        </div>}
        {this.state.thoughts && <div>
        {this.state.thoughts.map(thought => (
          <div key={thought.id}>
            {thought.thought}
            <br></br>
            {thought.submission_time}
            </div>

        ))}
        </div>}
      </Container>
    );
  }
}

export default App;
