import React from "react";
import { Link, Route } from "react-router-dom";
import ConvoDetail from "./ConvoDetail";

const Conversations = ({ match }) => {

    /* Create an array of `<li>` items for each product */
    const linkList = props.conversation.map((convo) => {
      return (
        <li>
          <Link to={`${match.url}/${convo.id}`}>{convo.title}</Link>
        </li>
      );
    });

    return (
      <div>
        <div>
          <div>
            <h3> Conversations</h3>
            <ul> {linkList} </ul>
          </div>
        </div>

        <Route
          path={`${match.url}/:convoId`}
          render={(props) => <ConvoDetail data={productsData} {...props} />}
        />
        <Route
          exact
          path={match.url}
          render={() => <div>Please select a product.</div>}
        />
      </div>
    );
  };

  export default Conversations
