import React from "react";
import axios from "axios";
import ConvoDetail from "./components/ConvoDetail";
import NavBar from "./components/navBar";
import "./App.css";
import {
  Container,
  Row,
  Col,
  Card,
  Modal,
  Form,
  Button,
  InputGroup,
  Navbar,
  Nav,
  NavDropdown,
  FormControl
} from "react-bootstrap";
import { BrowserRouter as Router, Route, NavLink } from "react-router-dom";
import DatePickerComponent from "./components/DatePicker";
import "react-datepicker/dist/react-datepicker.css";
import ConvoModal from "./components/convoModal";
import MessageModal from "./components/messageModal"


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isCreateConvo: false,
      isMessages: false,
      isThoughts: false,
      conversations: [],
      messages: [],
      thoughts: [],
      isOpenConvo: false,
      isOpenMessage: false,
      setIsOpenConvo: false,
      selectedDate: "",
      currConvoId: 0,
      currMessageId: 0,
      search: ''
    };
  }

  componentDidMount() {
    axios.get("http://localhost:8000/api/conversations/").then((response) => {
      const conversations = response.data;
      this.setState({ conversations });
    });
  }
  setSelectedDate = (date) => {
    this.setState({
      selectedDate: date,
    });
  };
  handleFormSubmit = (event) => {
    event.preventDefault();
    const title = event.target.elements.title.value;
    const start_date = event.target.elements.date.value;
    console.log(start_date);
    axios.post("http://localhost:8000/api/conversations/", {
      title: title,
      start_date: start_date,
      isOpenConvo: false,
    });
    axios.get("http://localhost:8000/api/conversations/").then((response) => {
      const conversations = response.data;
      this.setState({ conversations });
    });
  };

  onHandleChange = (e) => {
    this.setState({
      message: e.target.value,
    });
  };

  getConvoDetail = (convoId, convoTitle) => {
    console.log(convoId);
    // console.log(event)
    // event.preventDefault();
    axios
      .get(`http://localhost:8000/api/messages/${convoId}`)
      .then((response) => {
        const messages = response.data;
        this.setState({
          messages: messages,
          activeConvo: convoTitle,
          isMessages: true,
          currConvoId: convoId
        });
      });
  };

  getMessageDetail = (messId, messTitle) => {
    console.log(messId);
    // console.log(event)
    // event.preventDefault();
    axios
      .get(`http://localhost:8000/api/thoughts/${messId}`)
      .then((response) => {
        const thoughts = response.data;
        this.setState({
          thoughts: thoughts,
          activeMess: messTitle,
          isThoughts: true,
          currMessageId: messId
        });
      });
  };

  handleDownVote = (postId) => {
    axios.post(`http://localhost:8000/api/posts/${postId}/down_vote/`, {
      pk: postId,
    });
    axios.get("http://localhost:8000/api/posts/").then((response) => {
      const posts = response.data;
      this.setState({ posts });
    });
  };

  handleFeed = (feedName) => {
    axios.get(`http://localhost:8000/api/${feedName}/`).then((response) => {
      const posts = response.data;
      this.setState({ posts });
    });
  };

  toggleNewConvo = () => {
    if (this.state.isCreateConvo) {
      this.setState({
        isCreateConvo: false,
      });
    } else {
      this.setState({
        isCreateConvo: true,
      });
    }
  };

  showConvoModal = () => {
    this.setState({
      isOpenConvo: true,
    });
  };
  hideModal = () => {
    this.setState({
      isOpenConvo: false,
    });
  };
  hideMessageModal = () => {
    this.setState({
      isOpenMessage: false
    });
  }

  handleMessageFormSubmit = (event) => {
    event.preventDefault();
    console.log(event.target)
    const message = event.target.elements.message.value;
    const conversation = this.state.currConvoId
    axios.post("http://localhost:8000/api/messages/", {
      message: message,
      conversation: conversation,
      isOpenMessage: false,
    });
    axios.get("http://localhost:8000/api/messages/").then((response) => {
      const messages = response.data;
      this.setState({ messages });
    });
  }

  handleThoughtFormSubmit = (event) => {
    event.preventDefault();
    console.log(event.target)
    const thought = event.target.elements.thought.value;
    const message = this.state.currMessageId
    axios.post("http://localhost:8000/api/thoughts/", {
      thought: message,
      conversation: conversation,
      isOpenMessage: false,
    });
    axios.get("http://localhost:8000/api/messages/").then((response) => {
      const messages = response.data;
      this.setState({ messages });
    });
  }

  showMessageModal = () => {
    this.setState({
      isOpenMessage: true,
    });
  }

  updateSearch = (event) => {
    this.setState({
      search: event.target.value
    })
  }
  render() {
    let filteredConvos = this.state.conversations.filter(
      (convo) => {
        return convo.title.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1
      }
    )
    return (
      <Container fluid>
        <Navbar bg="light" expand="lg">
        <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="#home">Home</Nav.Link>
            <Nav.Link href="#link">Link</Nav.Link>
            <NavDropdown title="Dropdown" id="basic-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">
                Another action
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action/3.4">
                Separated link
              </NavDropdown.Item>
            </NavDropdown>
          </Nav>
          <Form inline>
            <FormControl type="text" placeholder="Search" className="mr-sm-2" value={this.state.search} onChange={this.updateSearch} name="searchbox" />
            <Button variant="outline-success">Search</Button>
          </Form>
        </Navbar.Collapse>
      </Navbar>
        <Container fluid>
        <Route
          path="/detail"
          render={() => (
            <ConvoDetail
              todos={this.state.activeConvo.filter(convo => convo.activeConvo === true)}
              handleToggleComplete={this.handleToggleComplete}
              handleDelete={this.handleDelete}
            />
          )}
        />
          <Row>
            <a href="#" onClick={this.showConvoModal}>
              <h1>New Conversation</h1>
            </a>
          </Row>
          <Row>
            {this.state.isCreateConvo && <div>ffff</div>}
            <Col sm={4}>
              {filteredConvos.map((convo) => (
                <Card style={{ width: "18rem", margin: "40px" }}>
                  <Card.Img variant="top" src="intro1.jpg" />
                  <Card.Body>
                    <Card.Title>
                      <NavLink
                        to="/convodetail"
                        onClick={() => {
                          this.getConvoDetail(convo.id, convo.title);
                        }}
                      >
                        <h1>{convo.title}</h1>
                      </NavLink>
                    </Card.Title>
                    <Card.Text>
                      <h3>{convo.start_date}</h3>
                      <p>{convo.id}</p>
                    </Card.Text>
                  </Card.Body>
                </Card>
              ))}
            </Col>
            <Col sm={4}>
              {this.state.messages && (
                <div>
                  {this.state.isMessages && (
                    <div>
                      <h3>Messages from: {this.state.activeConvo}</h3>
                      <a href="#" onClick={this.showMessageModal}>
                        <h3>New Message for {this.state.activeConvo}</h3>
                      </a>
                    </div>
                  )}
                  {this.state.messages.map((mess) => (
                    <div key={mess.id}>
                      <a
                        href="#"
                        onClick={() => {
                          this.getMessageDetail(mess.id, mess.message);
                        }}
                      >
                        {mess.message}
                      </a>
                      <br></br>
                      {mess.submission_time}
                      <br></br>
                      {mess.id}
                    </div>
                  ))}
                </div>
              )}
            </Col>
            <Col sm={4}>
              {this.state.thoughts && (
                <div>
                  {this.state.isThoughts && (
                    <h3>Thoughts from: {this.state.activeMess}</h3>
                  )}
                  {this.state.thoughts.map((thought) => (
                    <div key={thought.id}>
                      {thought.thought}
                      <br></br>
                      {thought.submission_time}
                    </div>
                  ))}
                </div>
              )}
            </Col>
            <ConvoModal
              {...this.state}
              handleFormSubmit={this.handleFormSubmit}
              showConvoModal={this.showConvoModal}
              hideModal={this.hideModal}
            />
            <MessageModal
              {...this.state}
              handleMessageFormSubmit={this.handleMessageFormSubmit}
              showConvoModal={this.showMessageModal}
              hideModal={this.hideMessageModal}
            />
            <ThoughtModal
              {...this.state}
              handleThoughtFormSubmit={this.handleThoughtFormSubmit}
              showConvoModal={this.showThoughtModal}
              hideModal={this.hideThoughtModal}
            />
          </Row>
        </Container>
      </Container>
    );
  }
}

export default App;
