import React from 'react';
import axios from "axios";
import ConvoDetail from './components/ConvoDetail'

import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      conversations: [],
      messages: "",
    };
  }

  componentDidMount() {
    axios.get("http://localhost:8000/api/conversations/").then((response) => {
      const conversations = response.data;
      this.setState({ conversations });
    });
  }

  handleFormSubmit = (event) => {
    event.preventDefault();
    const post_content = event.target.elements.post_content.value;
    const boast_or_roast = event.target.elements.boast_or_roast.value;
    axios.post("http://localhost:8000/api/posts/", {
      post_content: post_content,
      boast_or_roast: boast_or_roast,
    });
    axios.get("http://localhost:8000/api/posts/").then((response) => {
      const posts = response.data;
      this.setState({ posts, message: "" });
    });
  };

  onHandleChange = (e) => {
    this.setState({
      message: e.target.value,
    });
  };


  getConvoDetail = (e, convoId) => {
    e.preventDefault()
    axios.get(`http://localhost:8000/api/messages/${convoId}`).then((response) => {
      const messages = response.data;
      this.setState({ messages });
    });
  };

  handleDownVote = (postId) => {
    axios.post(`http://localhost:8000/api/posts/${postId}/down_vote/`, {
      pk: postId,
    });
    axios.get("http://localhost:8000/api/posts/").then((response) => {
      const posts = response.data;
      this.setState({ posts });
    });
  };

  handleFeed = (feedName) => {
    axios.get(`http://localhost:8000/api/${feedName}/`).then((response) => {
      const posts = response.data;
      this.setState({ posts });
    });
  };
  render() {

    return (
      <>
      {this.state.conversations.map(convo => (
        <div>
        <a href="#" onClick={this.getConvoDetail(convo.id)}><h1>{convo.title}</h1></a>
        <h3>{convo.start_date}</h3>
        <p>{convo.id}</p>
        </div>
      ))}
      {this.state.messages && <h1>fffff</h1>}
      </>
    );
  }
}

export default App;
