import React from "react";
import axios from "axios";
import ConvoDetail from "./components/ConvoDetail";
import NavBar from "./components/navBar";
import "./App.css";
import {
  Container,
  Row,
  Col,
  Card,
  Modal,
  Form,
  Button,
  InputGroup,
} from "react-bootstrap";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import DatePickerComponent from './components/DatePicker'
import 'react-datepicker/dist/react-datepicker.css'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isCreateConvo: false,
      isMessages: false,
      isThoughts: false,
      conversations: [],
      messages: [],
      thoughts: [],
      isOpen: false,
      setIsOpen: false,
      selectedDate:''
    };
  }

  componentDidMount() {
    axios.get("http://localhost:8000/api/conversations/").then((response) => {
      const conversations = response.data;
      this.setState({ conversations });
    });
  }
  setSelectedDate = date => {
    this.setState({
      selectedDate: date
    })
  }
  handleFormSubmit = (event) => {
    event.preventDefault();
    const title = event.target.elements.title.value
    const start_date = event.target.elements.date.value
    console.log(start_date)
    axios.post("http://localhost:8000/api/conversations/", {
      title: title,
      start_date: start_date,
    });
    axios.get("http://localhost:8000/api/conversations/").then((response) => {
      const conversations = response.data;
      this.setState({ conversations });
    });
  };

  onHandleChange = (e) => {
    this.setState({
      message: e.target.value,
    });
  };

  getConvoDetail = (convoId, convoTitle) => {
    console.log(convoId);
    // console.log(event)
    // event.preventDefault();
    axios
      .get(`http://localhost:8000/api/messages/${convoId}`)
      .then((response) => {
        const messages = response.data;
        this.setState({
          messages: messages,
          activeConvo: convoTitle,
          isMessages: true,
        });
      });
  };

  getMessageDetail = (messId, messTitle) => {
    console.log(messId);
    // console.log(event)
    // event.preventDefault();
    axios
      .get(`http://localhost:8000/api/thoughts/${messId}`)
      .then((response) => {
        const thoughts = response.data;
        this.setState({
          thoughts: thoughts,
          activeMess: messTitle,
          isThoughts: true,
        });
      });
  };

  handleDownVote = (postId) => {
    axios.post(`http://localhost:8000/api/posts/${postId}/down_vote/`, {
      pk: postId,
    });
    axios.get("http://localhost:8000/api/posts/").then((response) => {
      const posts = response.data;
      this.setState({ posts });
    });
  };

  handleFeed = (feedName) => {
    axios.get(`http://localhost:8000/api/${feedName}/`).then((response) => {
      const posts = response.data;
      this.setState({ posts });
    });
  };

  toggleNewConvo = () => {
    if (this.state.isCreateConvo) {
      this.setState({
        isCreateConvo: false,
      });
    } else {
      this.setState({
        isCreateConvo: true,
      });
    }
  };

  showModal = () => {
    this.setState({
      isOpen: true,
    });
  };
  hideModal = () => {
    this.setState({
      isOpen: false,
    });
  };
  render() {
    return (
      <Container fluid>
        <NavBar />
        <Container fluid>
          <Row>
            <a href="#" onClick={this.showConvoModal}>
              <h1>New Conversation</h1>
            </a>
          </Row>
          <Row>
            {this.state.isCreateConvo && <div>ffff</div>}
            <Col sm={4}>
              {this.state.conversations.map((convo) => (
                <Card style={{ width: "18rem", margin: "40px" }}>
                  <Card.Img variant="top" src="intro1.jpg" />
                  <Card.Body>
                    <Card.Title>
                      <a
                        href="#"
                        onClick={() => {
                          this.getConvoDetail(convo.id, convo.title);
                        }}
                      >
                        <h1>{convo.title}</h1>
                      </a>
                    </Card.Title>
                    <Card.Text>
                      <h3>{convo.start_date}</h3>
                      <p>{convo.id}</p>
                    </Card.Text>
                  </Card.Body>
                </Card>
              ))}
            </Col>
            <Col sm={4}>
              {this.state.messages && (
                <div>
                  {this.state.isMessages && (
                    <h3>Messages from: {this.state.activeConvo}</h3>
                  )}
                  {this.state.messages.map((mess) => (
                    <div key={mess.id}>
                      <a
                        href="#"
                        onClick={() => {
                          this.getMessageDetail(mess.id, mess.message);
                        }}
                      >
                        {mess.message}
                      </a>
                      <br></br>
                      {mess.submission_time}
                      <br></br>
                      {mess.id}
                    </div>
                  ))}
                </div>
              )}
            </Col>
            <Col sm={4}>
              {this.state.thoughts && (
                <div>
                  {this.state.isThoughts && (
                    <h3>Thoughts from: {this.state.activeMess}</h3>
                  )}
                  {this.state.thoughts.map((thought) => (
                    <div key={thought.id}>
                      {thought.thought}
                      <br></br>
                      {thought.submission_time}
                    </div>
                  ))}
                </div>
              )}
            </Col>
            <Modal show={this.state.isOpenConvo} onHide={this.hideModal}>
              <Modal.Header>
                <Modal.Title>New Conversation</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <Form onSubmit={this.handleFormSubmit}>
                  <Form.Group controlId="exampleForm.ControlTextarea1">
                    <Form.Label>Title</Form.Label>
                    <Form.Control
                      as="textarea"
                      rows="1"
                      maxLength="100"
                      name="title"
                    />
                    <DatePickerComponent />
                  </Form.Group>
                  <Button variant="primary" type="submit">
                    Submit
                  </Button>
                </Form>
              </Modal.Body>
              <Modal.Footer>
                <button onClick={this.hideModal}>Cancel</button>
                <button>Save</button>
              </Modal.Footer>
            </Modal>
          </Row>
        </Container>
      </Container>
    );
  }
}

export default App;
